INSERT INTO `ecm_job` VALUES ('1', '平面设计', '10', '北京', '8000', '1427280467', '1427280467', '<div style=\"font-family: simsun; margin: 0px; padding: 0px; color: #333333; line-height: 25px; min-height: 16px;\">\r\n<div style=\"margin: 0px; padding: 0px; min-height: 16px;\"><span style=\"font-family: 宋体;\">1、负责产品图片设计制作，用于京东、淘宝等电商平台销售；</span></div>\r\n<div style=\"margin: 0px; padding: 0px; min-height: 16px;\"><span style=\"font-family: 宋体;\">2、负责产品包装、丝印、说明书、彩页、展架设计；</span></div>\r\n<div style=\"margin: 0px; padding: 0px; min-height: 16px;\"><span style=\"font-family: 宋体;\">3、熟练操作PHOTOSHOP、CorelDRAW、动画制作软件等；</span></div>\r\n<div style=\"margin: 0px; padding: 0px; min-height: 16px;\"><span style=\"font-family: 宋体;\">4、美术专业毕业，从事设计工作3年以上；</span></div>\r\n<div style=\"margin: 0px; padding: 0px; min-height: 16px;\"><span style=\"font-family: 宋体;\">5、有良好的职业道德，工作积极主动；</span></div>\r\n<div style=\"margin: 0px; padding: 0px; min-height: 16px;\"><span style=\"font-family: 宋体;\">6、面试请带上5个以上以前完成的设计作品。</span></div>\r\n</div>\r\n<div style=\"font-family: simsun; margin: 0px; padding: 0px; color: #333333; line-height: 25px; min-height: 16px;\"><span style=\"font-family: 宋体;\"><br /></span></div>\r\n<div style=\"font-family: simsun; margin: 0px; padding: 0px; color: #333333; line-height: 25px; min-height: 16px;\"><span style=\"font-family: 宋体;\">视工作能力，待遇6000-10000元，社保齐全，工作餐补贴。</span></div>', '255');
INSERT INTO `ecm_job` VALUES ('2', '创意策划', '10', '北京', '8000', '1427280490', '1427280490', '<p><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">工作职责：</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">1、深入分析客户需求，准确把握客户品牌的特性、优势，提炼出适合于活动的与众不同的创意概念</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">2、线下活动的创意构思和方案撰写以及提案</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">&nbsp;</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">岗位要求：</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">1、2年以上的营销策划、公关活动策划经验&nbsp;</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">2、具有广泛的知识、敏锐的洞察力，清晰的思路和独具匠心的创意&nbsp;</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">3、有饱满的工作热情，创意思维活跃及优秀的文字驾驭能力&nbsp;</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">4、良好的语言表达能力</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">5、具备很强的整合传播理念，逻辑思维和PPT表现力，能够完美呈现完整策划案</span><br style=\"color: #333333; font-family: simsun; line-height: 25px;\" /><span style=\"color: #333333; font-family: simsun; line-height: 25px;\">6、良好的沟通能力和积极向上的团队精神，工作沟通配合能力强，有敬业精神，工作积极主动，有奉献精神，心理承受能力强，能适应较大的工作压力。</span></p>', '255');
INSERT INTO `ecm_job` VALUES ('3', '电子白板销售经理', '5', '北京', '5000', '1427280509', '1427280509', '<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">主要负责普罗米休斯电子白板的销售工作</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">35岁以内，男女不限，<br />统招大专以上学历，<br />熟悉现代教学理念，有丰富的教育行业客户基础。<br />从事电子白板一线销售工作三年以上，能够经常出差.</p>', '255');
INSERT INTO `ecm_job` VALUES ('4', '市场督导', '2', '北京', '3000', '1427280534', '1427280534', '<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">岗位职责</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">1、根据公司的安排，对相关区域市场进行现场巡查工作；</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">2、对业务的客情关系，配送能力，服务态度等进行调查和反馈；</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">3、发现市场问题并分析市场状况及时沟通汇报要求整改；</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">4、了解市场发展规划，公司下达的政策及营销方针的执行传达；</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">5、按时完成各类报表及上报资料，及领导分配的其他工作。</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">&nbsp;</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">岗位要求</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">1、大专及以上学历，1年以上相关工作经验优先；</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">2、有较强的沟通、协调能力，能承受较大的工作压力和挑战；</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">3、Office软件运用熟练，能适应经常出差；</p>\r\n<p style=\"font-size: 14px; font-family: simsun; margin: 0px; padding: 0px; font-stretch: normal; line-height: 25px; color: #333333;\">4、具有良好的口才和沟通能力，较强应变能力。</p>', '255');
